package com.yishan.algorithm;public class Fib {    public static long fib(int n) {        if (n == 0)            return 0;        if (n == 1)            return 1;        long fib = fib(n - 1) + fib(n - 2);        return fib;    }    public static void main(String[] args) {        for (int index = 0; index < 10; index++) {            System.out.println(fib(index));        }    }}