package com.yishan.algorithm;import java.util.PriorityQueue;public class FindKthLargest {    public static int findKthLargest(int[] arr, int k) {        PriorityQueue<Integer> q = new PriorityQueue<>(k);        for (int i : arr) {            q.offer(i);            if (q.size() > k) {                q.poll();            }        }        return q.peek();    }    public static void main(String args[]) {        int[] arr = {1, 1, 2, 3, 4, 2344,1101010101,423,123123,21312312,21312312,21312312,3123, 5};        System.out.println("The 2nd large value is:"+"\n"+findKthLargest(arr, 2)+"\n");    }    }