package com.yishan.algorithm;import java.util.Stack;public class FindSingleArray {    public int findArray(int[] sum) {        Stack<Integer> theArr = new Stack<>();        for (int i = 0; i < sum.length; i++) {            if (theArr.empty() || sum[i] != theArr.peek()) {                theArr.push(sum[i]);            } else {                theArr.pop();            }        }        return theArr.pop();    }    public static void main(String[] args) {        FindSingleArray finder = new FindSingleArray();        int[] si = {1, 1, 2, 3, 3, 4, 4, 8, 8};        int[] su = {13, 3, 7, 7, 10, 11, 11};        System.out.println("Example one is " + finder.findArray(si));        System.out.println("Example two is " + finder.findArray(su));    }}