package com.yishan.algorithm;public class RevIn {    static int result = 0;    public static int revIn(int n) {        while (n != 0) {            int tail = n % 10;            int newResult = result * 10 + tail;            result = newResult;            n = n / 10;        }        return result;    }    public static void main(String args[]){        revIn(678);        System.out.println("Result is:" + "\n" + result);    }}